    public static function get($name) {
        if (!isset(self::$icons[$name])) {
            return '';
        }
        // Garante que o SVG é retornado como uma string segura
        return trim(self::$icons[$name]);
    }

    public static function render($name) {
        // Adiciona um return false para evitar problemas de output buffering
        echo self::get($name);
        return false;
    }
