<?php
if(!is_user_logged_in()){
    ?>
    <script>
        window.location = "<?php echo esc_url(home_url('/login')); ?>";
    </script>
    <?php
    exit;
}
use playlist\Playlist;
use flash_message\FlashMessage;
use user\Users;
require_once get_template_directory() . '/components/player/Player.php';
require_once get_template_directory() . '/components/search/Search.php';
//pega a mensagem de sucesso, se existir
$success_message = FlashMessage::get('success');
if ($success_message) {
    FlashMessage::render($success_message);
}
// Pega a menagem de erro, se existir
$error_message = FlashMessage::get('error');
if ($error_message) {
    FlashMessage::render($error_message);
}
if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['create_playlist'])) {
    $is_premium = Users::check_user_premium_status();
    // Se não for premium, verificar quantidade de playlists
    if (!$is_premium) {
        $user_id = get_current_user_id();
        $user_playlists = get_posts([
            'post_type' => 'playlist',
            'author'    => $user_id,
            'post_status' => 'publish',
            'numberposts' => -1
        ]);
        if (count($user_playlists) >= 1) {
            FlashMessage::set('error', 'Go premium to create unlimited playlists.');
            ?>
            <script>window.location="<?php echo esc_url(home_url('/playlists')); ?>";</script> 
            <?php
            exit;
        }
    }
    $playlist = new Playlist();
    $result = $playlist->create_playlist([
        'title' => sanitize_text_field($_POST['playlist_title'])
    ]);
    if ($result['success']) {
        FlashMessage::set('success', '➡️ Playlist created successfully!');
        ?>
        <script>window.location="<?php echo esc_url(home_url('/playlists')); ?>";</script> 
        <?php
    } else {
        FlashMessage::set('error', $result['message'] ?? 'Não foi possível criar a playlist.');
        ?>
        <script>window.location="<?php echo esc_url(home_url('/playlists')); ?>";</script> 
        <?php
    }
}
